(85, 22, 0, '2016-05-30', 1, 1, 0, 'catalog/dong-ho-nam-automatic-03.JPG', 'Đồng hồ nam automatic ', ' Đồng hồ nam automatic được xem như là bộ máy lâu đời nhất trong 5 dạng máy đồng hồ chính', 'Đồng hồ nam automatic ', '0000-00-00', '', '', '', 0, 'dong-ho-nam-automatic-thu-choi-thoi-gian-bat-tan'),
(86, 22, 0, '2016-05-30', 1, 1, 9, 'catalog/dong-ho-nu-chinh-hang-01.jpg', 'đồng hồ nữ chính hãng', 'Với nhiều mẫu đồng hồ đẹp sang trọng thì đồng hồ nữ chính hãng dần trở thành một món đồ trang sức không thể thiếu của nữ giới.', 'Đồng hồ nữ chính hãng trang sức sang trọng hiện đại', '2016-05-31', '', '', '', 0, 'dong-ho-nu-chinh-hang-trang-suc-sang-trong-hien-dai'),
